// This file creates base64 placeholders for missing images
export const placeholderImage = 'data:image/svg+xml;base64,';

// Create a new image placeholder with specific dimensions and text
export const createPlaceholder = (width, height, text) => {
  return `data:image/svg+xml;base64,${btoa(
    `<svg width="${width}" height="${height}" xmlns="http://www.w3.org/2000/svg">
      <rect width="${width}" height="${height}" fill="#eeeeee"/>
      <text x="50%" y="50%" font-size="18" text-anchor="middle" alignment-baseline="middle" font-family="monospace, sans-serif" fill="#999999">${text || 'Image Placeholder'}</text>
    </svg>`
  )}`;
};

// Course placeholder with blue background
export const coursePlaceholder = 'data:image/svg+xml;base64,';
